void BalanzaInteligenteViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &btnSlot5)
    {
        //Slot5Config
        //When btnSlot5 clicked call virtual function
        //Call Slot5Config
        Slot5Config();
    }
    else if (&src == &btnSlot4)
    {
        //Slot4Config
        //When btnSlot4 clicked call virtual function
        //Call Slot4Config
        Slot4Config();
    }
    else if (&src == &btnSlot3)
    {
        //Slot3Config
        //When btnSlot3 clicked call virtual function
        //Call Slot3Config
        Slot3Config();
    }
    else if (&src == &btnSlot2)
    {
        //Slot2Config
        //When btnSlot2 clicked call virtual function
        //Call Slot2Config
        Slot2Config();
    }
    else if (&src == &btnSlot1)
    {
        //Slot1Config
        //When btnSlot1 clicked call virtual function
        //Call Slot1Config
        Slot1Config();
    }
    else if (&src == &btnPesar)
    {
        //CalcularPeso
        //When btnPesar clicked call virtual function
        //Call CalcularPeso
        CalcularPeso();
    }
    else if (&src == &BtnEnviar)
    {
        //Interaction1
        //When BtnEnviar clicked call virtual function
        //Call enviarMedianteSigfox
        enviarMedianteSigfox();
    }
    else if (&src == &cerrarModal)
    {
        //cerrarModal
        //When cerrarModal clicked hide alertaPerfil
        //Hide alertaPerfil
        alertaPerfil.setVisible(false);
        alertaPerfil.invalidate();
    }
    else if (&src == &cerrarModalEnvio)
    {
        //cerrarModal2
        //When cerrarModalEnvio clicked hide modalWindow1
        //Hide modalWindow1
        modalWindow1.setVisible(false);
        modalWindow1.invalidate();
    }
}

